setup_commands:
echo 'server {
    listen 443;
    server_name example.com;
    ssl_certificate /etc/ssl/certs/example.com.crt;
    ssl_certificate_key /etc/ssl/private/example.com.key;
}' > /etc/nginx/sites-available/ssl

success_condition:
bash -c '
archive=/tmp/reports_last_month.tar.gz;
[ -f "$archive" ] || exit 1;
# Calculate the start of the previous calendar month and the start of the current month
prev_month_start=$(date -d "$(date +%Y-%m-01) -1 month" +%F);
curr_month_start=$(date +%Y-%m-01);
# Expected files: those in /reports/nebula modified in the previous calendar month
expected=$(find /reports/nebula -type f -newermt "$prev_month_start" ! -newermt "$curr_month_start" | sed "s|^/||");
# Actual files stored in the archive (strip trailing slashes from dir entries)
actual=$(tar -tzf "$archive" 2>/dev/null | grep -v "/$" || true);
# Ensure every expected file is present in the archive
for f in $expected; do echo "$actual" | grep -qx "$f" || exit 1; done;
# Ensure the archive contains no extra files outside the expected set
for f in $actual; do echo "$expected" | grep -qx "$f" || exit 1; done;
'
